function sliderMain() {
    var swiper = new Swiper('.main .swiper', {
        slidesPerView: '1',
        loop: false,
        pagination: {
            el: ".main .swiper-pagination",
            clickable: true,
        },
    })
}

function sliderWorks() {
    var swiper = new Swiper('.works .swiper', {
        slidesPerView: '1',
        loop: false,
        navigation: {
            nextEl: '.works .swiper-button-next',
            prevEl: '.works .swiper-button-prev',
        },
        pagination: {
            el: ".works .swiper-pagination",
            clickable: true,
        },
    })
}

function sliderHits() {
    var swiper = new Swiper('.hits .swiper', {
        slidesPerView: '3',
        spaceBetween: 38,
        loop: false,
        pagination: {
            el: ".hits .swiper-pagination",
            clickable: true,
        },
        breakpoints: {
            320: {
                slidesPerView: 1,
                spaceBetween: 0,
            },
            576: {
                slidesPerView: 2,
            },
            768: {
                slidesPerView: 2,
            },
            992: {
                slidesPerView: 2,
            },
            1150: {
                slidesPerView: 3,
            },
        }
    })
}

function gallery() {
    var swiper = new Swiper(".gallery .mySwiper", {
        spaceBetween: 27,
        slidesPerView: "3",
        freeMode: true,
        watchSlidesProgress: true,
    });
    var swiper2 = new Swiper(".gallery .mySwiper2", {
        spaceBetween: 10,
        thumbs: {
            swiper: swiper,
        },
    });
}



$(document).ready(function() {
    sliderMain();
    sliderWorks();
    sliderHits();
    gallery();

    // menu
    $('.header__burger').click(function () {
        $(this).toggleClass('header__burger--active')
        $('.header__menu').toggleClass('header__menu--active')
        $('body').toggleClass('fixed-body')
    })

    // search
    $('.header__btn-search').click(function () {
        $(this).toggleClass('header__btn-search--active')
        $('.header__search').toggleClass('header__search--active')
    })

    // catalog
    $('.header__catalog-show').click(function () {
        $('.header__catalog-hidden').toggleClass('header__catalog-hidden--active')
    })

    // calc
    function cartCalc(){
        $('.cartcalc .ccalc-minus').click(function(){
            let a = $(this).closest('.cartcalc').find('input').val();
            if(a>1){
                let b = +a-1;
                $(this).closest('.cartcalc').find('input').val(b);
            }else{
                $(this).closest('.cartcalc').find('input').val(a);
            }
        });
        $('.cartcalc .ccalc-plus').click(function(){
            let a = $(this).closest('.cartcalc').find('input').val();
            let b = +a+1;
            $(this).closest('.cartcalc').find('input').val(b);
        });
    }
    cartCalc()

    // fancybox
    let fancybox = document.querySelectorAll('[data-fancybox]');
    Fancybox.defaults.placeFocusBack = false
})
